CREATE table Class_Info(
	ClassID int not NULL PRIMARY KEY COMMENT'本条记录编号',
	Classint VARCHAR(50) not NULL COMMENT'板机编号（名称）',
	CTeacherID INT  not NULL COMMENT'班主任ID，外检，引用teacherinfo表的主键teacherid',
	ClassGrade VARCHAR(20) not null COMMENT'班级所在年级，默认为s1，取值范围在s1 s2 y2之间'
)
SELECT * FROM Class_Info;
DESC Class_Info;




CREATE table Teacher_Info(
	TeacherID int not null PRIMARY KEY COMMENT'本条记录编号',
	TeacherName VARCHAR(50) not null COMMENT'班主任姓名',
	TeacherTel VARCHAR(50) COMMENT'班主任电话',
	TeacherEmail VARCHAR(50)  COMMENT'班主任电子邮箱，邮件'
)
SELECT * FROM Teacher_Info;
DESC Teacher_Info;







create table stu3(
StuID int not null COMMENT'本条记录编号' PRIMARY KEY,
Stuint VARCHAR(10)  not NULL  COMMENT'学员学号',
StuSName VARCHAR(32) not NULL  COMMENT'学员姓名',
StuAge int COMMENT'学员年龄，必须在16-35岁之间',
StuSex VARCHAR(2) not NULL  COMMENT'学院性别，默认为‘男’，取值范围在‘男’或‘女’之间',
StuCard VARCHAR(20)  COMMENT'学院身份证号码',
StuJoinTime DATE  not NULL  COMMENT'学员入学时间',
StuAddress VARCHAR(50)  COMMENT'学院家庭住址',
SClassID int   COMMENT'学院所在班级ID，外键，引用ClassInfo表的主键ClassID'
)
