创建班级信息表
create table ClassInfo(
ClassID int  not NULL  COMMENT'本条记录编号' PRIMARY KEY,
Classint VARCHAR(20)  not NULL  COMMENT'班级编号（名称）',
CTeacherID INT  not NULL  COMMENT'班主任ID，外键，引用TeacherInfo表的主键TeacherID',
ClassGrade VARCHAR(2) not NULL DEFAULT S1  COMMENT'班级所在年级，默认为‘S1’，取值范围在‘S1’‘S2’‘Y2’之间'
)

创建班主任信息表
create  TeacherInfo(
TeacherID int not null '本条记录编号',
TeacherName VARCHAR(20) int not null '班主任姓名',
TeacherTel VARCHAR(20) COMMENT'班主任电话',
TeacherEmail varchar(20)  COMMENT'班主任电子邮件，邮箱必须含有‘@’符号 ',
)

创建学院成绩表
create table StudentExam(
ExamID int int not null '本条记录编号' PRIMARY KEY,
Examint VARCHAR(32) int not null '本次考试代号',
EStuID int int not null '学员信息ID，外键，引用StudentInfo表的主键StuID',
ExamSubject varchar(20) int not null '本次考试成成名称',
ExamResult int COMMENT'学院成绩，取值返回在0-100分之间'
)



















