创建数据库为j02
CREATE DATABASE j02;
创建mysql用户  用户名为stuDBA 密码为stu
create user 'stuDBA'@'localhost' identified by 'stu';
给刚创建的用户授权
grant all privileges on *.* to 'stuDBA'@'localhost' identified by 'stu' with grant option;
刷新权限
FLUSH PRIVILEGES;
查询当前用户的权限
show GRANTS;
查询mysql数据所有的权限
show PRIVILEGES;
REVOKE SELECT,UPDATE  on *.* from 'stuDBA'@'localhost'; 





